  <label for="exampleFormControlInput1" class="form-label">ingredient:</label>
  <input type="text" class="form-control" id="exampleFormControlInput1" placeholder="ingredient ">
</div>
<div class="mb-3">
  <label for="exampleFormControlTextarea1" class="form-label">recette</label>
  <textarea class="form-control" id="exampleFormControlTextarea1" rows="3"></textarea>
</div>
<div class="mb-3">
  <label for="exampleFormControlInput1" class="form-label">temps:</label>
