import { Component, OnInit } from '@angular/core';
import { Plat } from 'src/app/models/plat.models';
import { PlatService } from 'src/app/services/plat.service';

@Component({
  selector: 'app-body',
  templateUrl: './body.component.html',
  styleUrls: ['./body.component.css']
})
export class BodyComponent implements OnInit {

  protected plats: Plat[] = [];
   constructor(private platService: PlatService){}
  ngOnInit(): void {
    this.fetchAllPlat();
  }

  fetchAllPlat():void{
    this.platService.getAllPlat().subscribe(plats => {
      this.plats = plats;
    });
  }
}


<div class="container-fluid text-center">
  <div class="row">
    <h3>bienvenue sur votre site de recette de cuisines préféré</h3>
  </div>
  <div class="row" >
    <div class="col" *ngFor="let plat of plats">
    <div class="card" style="width: 18rem;">
    <img src="assets/images/plat-feuilles-de-manioc-poisson-fume.jpg" class="card-img-top">
    <div class="card-body">
      <h5 class="card-title">{{plat.nom}}</h5>
      <p class="card-text">{{plat.description}}</p>
      <a href="#" class="btn btn-primary">voir la recette</a>
    </div>
  </div>
  </div>
  </div>
</div>
