import { Component, OnInit } from '@angular/core';
import { Plat } from 'src/app/models/plat.models';
import { PlatService } from 'src/app/services/plat.service';

@Component({
  selector: 'app-body',
  templateUrl: './body.component.html',
  styleUrls: ['./body.component.css']
})
export class BodyComponent implements OnInit {

  private plats: Plat[] = [];
  protected constructor(private platService: PlatService){}
  ngOnInit(): void {

  }

  fetchAllPlat():void{
    this.platService.getAllPlat().subscribe(plats => {
      this.plats = plats;
    });
  }

  fetchUserAll():void{
    this.userService.getAllUsers().subscribe(users => {
      this.users = users;
    });
}


<div class="container-fluid text-center">
  <div>
    <h3>bienvenue sur votre site de recette de cuisines préféré</h3>
  </div>
  <div>
    <div class="card" style="width: 18rem;">
    <img src="assets/images/plat-feuilles-de-manioc-poisson-fume.jpg" class="card-img-top">
    <div class="card-body">
      <h5 class="card-title">Card title</h5>
      <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
      <a href="#" class="btn btn-primary">Go somewhere</a>
    </div>
  </div>
  </div>
</div>
