    <img src="assets/images/plat-feuilles-de-manioc-poisson-fume.jpg" class="card-img-top">
    <div class="card-body">
      <h5 class="card-title">{{plat.nom}}</h5>
      <p class="card-text">{{plat.description}}</p>
      <a href="#" class="btn btn-primary">voir la recette</a>
    </div>
  </div>
